import React, { useState } from 'react';
import ExpenseItem from './components/Expenses/ExpenseItem';
import NewExpense from './components/NewExpense/NewExpense';
import Expenses from "./components/Expenses/Expenses"


const  DUMMY_EXPENSES = [
  {id : 'e1', title: 'Toilet Paper', amount : 94.12, date: new Date(2021, 2, 28)},
  {id : 'e2', title: 'Car Insurance', amount : 294.67, date: new Date(2022, 3, 28)},
  {id : 'e3', title: 'New TV', amount : 799.49, date: new Date(2020, 4, 28)},
  {id : 'e4', title: 'New Desk (wooden)', amount : 450, date: new Date(2022, 8, 8)},
]

const App = () => {

  const [expenses, setExpenses] = useState(DUMMY_EXPENSES)

  const addExpenseHandler = expense => {
    setExpenses(prevExpenses => {
      return [expense, ...prevExpenses];
    });
  }

  //return React.createElement(
  //  'div',
  //  {},
  //  React.createElement('h2', {}, "let's get started!"),
  //  React.createElement(Expenses, {items: expenses})
  //);

  return (
    <div>
      <NewExpense onAddExpense = {addExpenseHandler}/>
      <Expenses items={expenses} />
    </div>
  );
}

export default App;
